import React, { useState } from 'react';
import '../App.css';

export default function Signup({ onBack }) {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirm, setConfirm] = useState('');

  function submit(e) {
    e.preventDefault();
    if (password !== confirm) {
      alert('Passwords do not match');
      return;
    }
    console.log('Signup', { name, email });
    alert('Signup submitted (check console)');
  }

  return (
    <div className="auth-screen gradient-bg-full">
      <div className="auth-card">
        <div className="auth-card-bar top" />
        <div className="auth-card-body">
          <button className="link-back" onClick={onBack}>&larr; Back</button>
          <h3 className="auth-title">Create an account</h3>
          <form className="auth-form" onSubmit={submit}>
            <label className="field">
              <span>Full name</span>
              <input value={name} onChange={e => setName(e.target.value)} required />
            </label>
            <label className="field">
              <span>Email</span>
              <input type="email" value={email} onChange={e => setEmail(e.target.value)} required />
            </label>
            <label className="field">
              <span>Password</span>
              <input type="password" value={password} onChange={e => setPassword(e.target.value)} required />
            </label>
            <label className="field">
              <span>Confirm password</span>
              <input type="password" value={confirm} onChange={e => setConfirm(e.target.value)} required />
            </label>

            <button className="btn primary block" type="submit">Sign up</button>
            <div className="divider">or</div>
            <button type="button" className="btn google" onClick={() => alert('Google signup (not implemented)')}>Sign up with Google</button>
          </form>
        </div>
        <div className="auth-card-bar bottom" />
      </div>
    </div>
  );
}
